CREATE TABLE `cadastro` (
   `nome` varchar(100),
   `sobrenome` varchar(100),
   `cpf` int(11) not null auto_increment,
   `telefone` int(15),
   `email` varchar(50),
   `endereco` varchar(200),
   `cep` int(15),
   `cidade` varchar(30),
   `estado` varchar(2),
   `pedido` varchar(300),
   PRIMARY KEY (`cpf`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=101;

